mapper.update:
update ge_policy_detail
set
  status = #{status,jdbcType=VARCHAR }
where id = #{id,jdbcType=INTEGER}

mapper.select:
select

from ge_policy_detail d
where id = #{id,jdbcType=INTEGER}

select
,

from ge_policy_detail d left join ge_policy p on d.id = p.policy_detail_id limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select
,

from ge_policy_detail d left join ge_policy p on d.id = p.policy_detail_id where d.status = #{status,jdbcType=VARCHAR} limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
