insert into ge_policy_raw (id, crawl_url, crawl_time, index_number, title, release_office, release_number, policy_basis, wiritten_time, release_time, effect_time, content, image_url, status, html, remark) values (31, 'http://www.gov.cn/zhengce/content/2018-12/24/content_5351573.htm', '2019-02-17 15:27:18', '000014349/2004-00205', '国务院办公厅关于同意以深圳市名义申办2011年第26届世界大学生夏季运动会的复函', '国务院办公厅', '国办函〔2004〕88号', null, '2005-01-17 15:27:18', '2019-01-24 15:27:18', null, '<tbody> 
 <tr> 
  <td class="b12c" id="UCAP-CONTENT"><p style="margin-top: 0px; margin-bottom: 0px; text-align: center; font-family: 宋体;"><span style="font-size: 18pt; font-weight: bold;">国务院办公厅关于同意以深圳市名义</span></p> <p style="margin-top: 0px; margin-bottom: 0px; text-align: center; font-family: 宋体;"><span style="font-size: 18pt; font-weight: bold;">申办2011年第26届世界大学生</span></p> <p style="margin-top: 0px; margin-bottom: 0px; text-align: center; font-family: 宋体;"><span style="font-size: 18pt; font-weight: bold;">夏季运动会的复函</span></p> <p style="margin-top: 0px; margin-bottom: 0px; text-align: center;"><span style="font-family: 楷体, 楷体_GB2312;"><span style="color: rgb（0, 0, 0）; font-family: 楷体, 楷体_GB2312; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 35px; orphans: auto; text-align: center; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; display: inline !important; float: none; background-color: rgb（255, 255, 255）;">国办函〔2004〕88号</span></span></p> <p align="" style="margin-top: 0px; margin-bottom: 0px; text-align: justify; font-family: 宋体;"><br> </p> <p align="" style="margin-top: 0px; margin-bottom: 0px; text-align: justify; font-family: 宋体;">广东省人民政府，教育部、外交部、财政部、体育总局：</p> <p align="" style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: justify; font-family: 宋体;">教育部、外交部、财政部、体育总局《关于以深圳市名义申办2011年世界大学生夏季运动会的请示》（教外际〔2004〕81号）收悉。经国务院领导同志批准，现函复如下：</p> <p align="" style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: justify; font-family: 宋体;">同意以深圳市名义申办2011年第26届世界大学生夏季运动会（以下简称运动会）。运动会申办全部经费由广东省人民政府承担；运动会举办的主要经费由广东省人民政府承担，中央财政将视情况对国际大体联官员、特邀贵宾的接待费给予一定补助。请有关方面抓紧向国际大体联申报。</p> <p style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: right; font-family: 宋体;">国务院办公厅 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p> <p style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: right; font-family: 宋体;">二〇〇四年十二月十七日 &nbsp; &nbsp; &nbsp;&nbsp;</p></td> 
 </tr> 
 <tr> 
  <td></td> 
 </tr> 
</tbody>', null, '未处理', '<div class="wrap" style="font-family:宋体;"> 
 <table width="724" border="0" cellpadding="0" cellspacing="0" style="border:1px solid #999999;margin:0 auto;margin-top:10px;"> 
  <tbody> 
   <tr> 
