insert into ge_policy_raw (id, crawl_url, crawl_time, index_number, title, release_office, release_number, policy_basis, wiritten_time, release_time, effect_time, content, image_url, status, html, remark) values (43, 'http://www.gov.cn/zhengce/content/2018-12/03/content_5345413.htm', '2019-02-17 15:34:02', '000014349/1996-00074', '国务院办公厅关于广东与香港、澳门合建自动气象站网有关问题的复函', '国务院办公厅', '国办函〔1996〕14号', null, '1996-03-06 15:34:02', '2019-01-03 15:34:02', null, '<tbody> 
 <tr> 
  <td class="b12c" id="UCAP-CONTENT"><p style="margin-top: 0px; margin-bottom: 0px; text-align: center; font-family: 宋体;"><span style="font-weight: bold; font-size: 18pt;">国务院办公厅关于广东与香港、澳门合建</span></p> <p style="margin-top: 0px; margin-bottom: 0px; text-align: center; font-family: 宋体;"><span style="font-weight: bold; font-size: 18pt;">自动气象站网有关问题的复函</span></p> <p style="margin-top: 0px; margin-bottom: 0px; text-align: center;"><span style="font-family: 楷体, 楷体_GB2312;">国办函〔1996〕14号</span></p> <p style="margin-top: 0px; margin-bottom: 0px; text-align: center; font-family: 宋体;"><br> </p> <p align="" style="margin-top: 0px; margin-bottom: 0px; text-align: justify; font-family: 宋体;">中国气象局：</p> <p align="" style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: justify; font-family: 宋体;">你局《关于广东与香港澳门合建自动气象站网的请<span style="text-indent: 2em;">示》收悉。经与有关部门协商，并报经国务院批准，现就有关事项答复如下：</span></p> <p align="" style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: justify; font-family: 宋体;">一、同意广东省气象局与香港天文台合建3个自动气象站；与澳门地球物理气象台合建2个自动气象站。</p> <p align="" style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: justify; font-family: 宋体;">二、气象站网组建中的有关具体事宜，请中国气象局按照《国务院批转外交部和国务院港澳办公室关于内地同香港、澳门之间官方往来问题请示的通知》（国发〔1986〕97号）和有关规定精神，与有关部门和地方政府协商办理。气象站建设的具体实施方案，按基建程序报批。</p> <p align="" style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: justify; font-family: 宋体;">三、合建的气象专用网，应按有关通信出入境管理的规定办理审批手续，并报国家安全部门和通信主管部门备案。</p> <p align="" style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: justify; font-family: 宋体;">四、自动气象站的建设要尽量避开军事设施和军事设施保护区域。勘察定点和制定建设规划、管理规定要征求军队主管部门的意见。上岛勘察设计和施工作业及建成后的管理，应由内地人员承担，港澳人员必须上岛时，应征得广州军区同意并办理有关手续。</p> <p style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: right; font-family: 宋体;">国务院办公厅 &nbsp; &nbsp; &nbsp; &nbsp;</p> <p style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: right; font-family: 宋体;">一九九六年二月六日&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p> <p style="margin-top: 0px; margin-bottom: 0px; text-indent: 2em; text-align: left; font-family: 宋体;">（本文有删减）</p></td> 
 </tr> 
 <tr> 
  <td></td> 
 </tr> 
</tbody>', null, '未处理', '<div class="wrap" style="font-family:宋体;"> 
